mapper.select:
SELECT a.* FROM codeCd a
WHERE 1 = 1
AND cSeq = #{cSeq}

mapper.insert:

		INSERT INTO codeCd(

        cdName
        ,useNY
        ,delNY
        ,codeGroup_cgSeq

        )
        VALUES (

            #{cdName}
           ,#{useNY}
           ,#{delNY}
           ,#{cgSeq}
        )

        		SELECT last_insert_id()

mapper.update:

	update 
		codeCd
	set
		cdName = #{cdName}
        ,useNY = #{useNY}
        ,delNY = #{delNY}
        ,codeGroup_cgSeq = #{cgSeq}
	where 
		cSeq = 16
