mapper.insert:

		INSERT INTO codeCd(

        cdName
        ,useNY
        ,delNY
        ,codeGroup_cgSeq

        )
        VALUES (

            #{cdName}
           ,#{useNY}
           ,#{delNY}
           ,(select cgSeq from codeGroup where cgName = #{codeGroup_cgSeq})
        )

        		SELECT last_insert_id()
