mapper.select:
SELECT aa.* from (
	SELECT
		a. *
		,(SELECT COUNT(aa.cSeq) FROM codeCd aa WHERE 1=1 AND aa.delNY = 0 AND aa.codeGroup_cgSeq = a.cgSeq) as total

	FROM codeGroup a

	WHERE 1=1

			 AND  a.useNY = #{shUse} 


				AND a.cgSeq = #{shValue}
				AND a.cgName LIKE CONCAT('%',#{shValue},'%')
				AND a.cgKor LIKE CONCAT('%',#{shValue},'%')



		ORDER BY a.cgSeq DESC limit 99999999999

		) aa

		limit #{rowNumToShow} offset #{startRnumForMysql}

mapper.insert:

		INSERT INTO codeGroup(

        cgName
        ,cgKor
        ,useNY
		,delNY
		,startDate
        )

        VALUES (

            #{cgName}
            , #{cgKor}
           	, #{useNY}
           	, #{delNY}
           	, #{startDate}
        )

        		SELECT last_insert_id()

mapper.update:

UPDATE codeGroup

SET
	cgName =  #{cgName}
	, cgKor = #{cgKor}
	, useNY = #{useNY}
	, delNY = #{delNY}
	, startDate = #{startDate}

WHERE cgSeq = #{cgSeq}
